rule.example:
class Something
{
    private static $FOO = 2; // Unused
    private $i = 5; // Unused
    private $j = 6;
    public function addOne()
    {
        return $this->j++;
    }
}

class Foo {
    public function doSomething()
    {
        $i = 5; // Unused
    }
}

class Something
{
    private function foo() {} // unused
}

class Foo
{
    private function bar($howdy)
    {
        // $howdy is not used
    }
}
